import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import Login from "./Login";


export class Main extends Component {
    state = {
        step: 1,
        user: "",
        password: "",
        confirmPassword: "",
        email: "",
        placeName: "",
        placeDescription: "",
        placePhone: ""
    }

    //Metodo que procede para a prox. pagina
    nextStep = () => {
        const { step } = this.state;
        this.setState({
            step: step + 1
        });
        console.log(step);
    }

    //Metodo que retorna a pagina anterior
    prevStep = () => {
        const { step } = this.state;
        this.setState({
            step: step - 1
        });
    }

    //Metodo para lidar com as mudancas nos campos do formulario
    //Input e a variavel que sera mudada dentro do objeto state, e eh o evento que mudaram a variavel
    handleChange = input => e => {
        this.setState({ [input]: e.target.value });
        console.log(this.state.user);
        console.log(this.state.senha);
    }

    render() {
        const { step } = this.state;
        const { user, password, confirmPassword, email, placeName, placeDescription, placePhone } = this.state;
        const values = { user, password, confirmPassword, email, placeName, placeDescription, placePhone }

                return (
                    <Router>
                        <Login
                            nextStep={this.nextStep}
                            handleChange={this.handleChange}
                            values={values}
                        />
                    </Router>
                )

    }
}

export default Main
