import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import Login from "./Login";
import Cad from './Cad'
import Config from './Config'

export class Main extends Component {
    state = {
        user: "",
        password: "",
        confirmPassword: "",
        email: "",
        placeName: "",
        placeDescription: "",
        placePhone: ""
    }

    handleChange = input => e => {
        this.setState({ [input]: e.target.value });
        console.log(this.state.user);
        console.log(this.state.senha);
    }



    render() {
        const { user, password, confirmPassword, email, placeName, placeDescription, placePhone } = this.state;
        const values = { user, password, confirmPassword, email, placeName, placeDescription, placePhone }
        const LoginPage = (props) => {
            return (
              <ProductPage 
                toggleSidebarOn={this.toggleSidebarOn.bind(this)}
                {...props}
              />
            );
          }

        return (
            <Router>
                <React.Fragment>
                    <Login
                        handleChange={this.handleChange}
                        values={values}
                    />
                    <Route path="/login" render={LoginPage}
                    />
                    <Route path="/cadastro" component={Cad} />
                    <Route path="/config" component={Config} />
                </React.Fragment>
            </Router>
        )

    }
}

export default Main
