import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import MuiThemeProvider from '@material-ui/core/styles/MuiThemeProvider';
import { createMuiTheme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core//Typography'
import red from '@material-ui/core/colors/red'
import Login from "./Login";
import Cad from './Cad'
import Config from './Config'
import Home from "./Home";

const theme = createMuiTheme({
    palette: {
        primary: {
            main: red['A700']
        },
        secondary: {
            main: red[900]
        }
    }
});


export class Main extends Component {
    state = {
        user: "",
        password: "",
        confirmPassword: "",
        email: "",
        placeName: "",
        placeDescription: "",
        placePhone: ""
    }

    handleChange = input => e => {
        this.setState({ [input]: e.target.value });
        console.log(this.state.user);
        console.log(this.state.senha);
    }



    render() {
        const { user, password, confirmPassword, email, placeName, placeDescription, placePhone } = this.state;
        const values = { user, password, confirmPassword, email, placeName, placeDescription, placePhone }
        const LoginPage = (props) => {
            return (
                <Login
                    handleChange={this.handleChange}
                    values={values}
                />
            );
        }
        return (
            <Router>
                <MuiThemeProvider theme={theme}>
                    <React.Fragment>
                        <AppBar position="static" color="primary" >
                            <Toolbar >
                                <Typography variant="h5">
                                    WebMenu
                            </Typography>
                            </Toolbar>
                        </AppBar>
                        <Switch>
                             <Route path="/" component={Home} />
                            <Route path="/login" render={LoginPage} />
                            <Route path="/cadastro" component={Cad} />
                            <Route path="/config" component={Config} />
                        </Switch>
                    </React.Fragment>
                </MuiThemeProvider>
            </Router>
        )

    }
}

export default Main
